'use strict';

var vue = require('vue');

function useAuth(auth) {
  const user = vue.ref(auth.currentUser);
  const isAuthenticated = vue.computed(() => !!user.value);
  auth.onIdTokenChanged((authUser) => user.value = authUser);
  return {
    isAuthenticated,
    user
  };
}

exports.useAuth = useAuth;
